import React, { Component } from "react";

export default class PlaceOrder extends Component {
    // salesPersonId, product, quantity
    constructor(props) {
        super(props);
        this.handleSubmitOrder = this.handleSubmitOrder.bind(this);
        this.onChangeSalesPerson = this.onChangeSalesPerson.bind(this);
        this.onChangeProduct = this.onChangeProduct.bind(this);
        this.onChangeQuantity = this.onChangeQuantity.bind(this);

        // Get tutorial data from URL parameters
        const params = new URLSearchParams(window.location.search);
        this.state = {
            currentOrder: {
                sales_person: '',
                product: '',
                quantity: ''
            }
        };
    }

    handleSubmitOrder() {
        // Add your order submission logic here
        alert("Order submitted successfully!");
        window.close();
    }

    onChangeSalesPerson(e) {
        const sales_person = e.target.value;
        this.setState(prevState => ({
            currentOrder: {
              ...prevState.currentOrder,
              sales_person: sales_person
            }
          }));
    }


    render() {
        return (
            <div className="container">
                <h2 className="text-center">Order Details</h2>
                <div className="card">
                    <div className="card-body">
                        <div className="form-group">
                            <label><strong>Title:</strong></label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.title}
                            />
                        </div>

                        <div className="form-group">
                            <label><strong>Description:</strong></label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.description}
                            />
                        </div>

                        <div className="form-group">
                            <label><strong>Status:</strong></label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.status}
                            />
                        </div>

                        <div className="text-center mt-3">
                            <button
                                className="btn btn-success"
                                onClick={this.handleSubmitOrder}
                            >
                                Submit Order
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
